// Extract domains from incoming request
const domains = $input.first().json.body.domains || [];

// Return each domain as separate item for processing
return domains.map(domain => ({
  json: {
    domain: domain.replace(/^https?:\/\//, '').replace(/^www\./, '').split('/')[0],
    originalDomain: domain
  }
}));

// Process the Google Ads Transparency response
const domain = $input.first().json.domain;
const originalDomain = $input.first().json.originalDomain;

let hasAds = false;
let adDetails = null;
let confidence = 0;
let error = null;

try {
  const response = $input.first().json;

  // Check if we got a valid response with ad data
  if (response.data) {
    const data = typeof response.data === 'string' ? response.data : JSON.stringify(response.data);

    // Look for indicators of active ads
    if (data.includes('creative') || data.includes('advertiser')) {
      hasAds = true;
      confidence = 80;
      adDetails = 'Active ads detected in transparency center';
    }
  }

  // If HTTP request failed, check the error
  if (response.error) {
    error = response.error;
    confidence = 0;
  }

} catch (e) {
  error = e.message;
}

return [{
  json: {
    domain,
    originalDomain,
    signal: 'googlePaidTraffic',
    detected: hasAds,
    confidence,
    details: adDetails,
    error,
    checkedAt: new Date().toISOString()
  }
}];

// Analyze website for additional signals
const domain = $input.first().json.domain;
const html = $input.first().json.data || '';

// Detect chat widgets (After Hours Coverage signal)
const chatWidgets = {
  drift: /drift\.com|dc\.js|driftt/i,
  intercom: /intercom\.com|intercom\.io|widget\.intercom/i,
  zendesk: /zendesk\.com|zopim|zdassets/i,
  hubspot: /hubspot\.com\/conversations|hs-scripts/i,
  freshdesk: /freshdesk\.com|freshchat/i,
  crisp: /crisp\.chat|crisp\.im/i,
  livechat: /livechatinc\.com|livechat/i,
  olark: /olark\.com/i,
  tawk: /tawk\.to/i
};

let chatWidgetDetected = null;
for (const [name, pattern] of Object.entries(chatWidgets)) {
  if (pattern.test(html)) {
    chatWidgetDetected = name;
    break;
  }
}

// Detect booking tools
const bookingTools = {
  calendly: /calendly\.com/i,
  hubspotMeetings: /meetings\.hubspot\.com/i,
  chilipiper: /chilipiper\.com/i,
  acuity: /acuityscheduling\.com/i,
  savvycal: /savvycal\.com/i
};

let bookingToolDetected = null;
for (const [name, pattern] of Object.entries(bookingTools)) {
  if (pattern.test(html)) {
    bookingToolDetected = name;
    break;
  }
}

// Check for forms (potential inbound response risk)
const hasContactForm = /contact-form|wpcf7|form.*contact|hubspot.*form/i.test(html);

return [{
  json: {
    domain,
    websiteAnalysis: {
      chatWidget: chatWidgetDetected,
      hasChatWidget: !!chatWidgetDetected,
      bookingTool: bookingToolDetected,
      hasBookingTool: !!bookingToolDetected,
      hasContactForm
    }
  }
}];

// Combine all signals and calculate score
const items = $input.all();
const adsData = items[0]?.json || {};
const websiteData = items[1]?.json?.websiteAnalysis || {};

const domain = adsData.domain;

// Build signals array
const signals = [];

// Google Paid Traffic signal
if (adsData.detected) {
  signals.push({
    id: 'googlePaidTraffic',
    label: 'Google Paid Traffic Active',
    detected: true,
    value: adsData.details || 'Active Google Ads detected',
    confidence: adsData.confidence
  });
}

// After Hours Coverage Gap signal
// If they have chat but no 24/7 indicator, flag as potential gap
if (websiteData.hasChatWidget) {
  signals.push({
    id: 'afterHoursCoverage',
    label: 'After Hours Coverage Gap',
    detected: true,
    value: `Uses ${websiteData.chatWidget} - verify after-hours coverage`,
    confidence: 60
  });
}

// Inbound Response Risk
// If they have forms but no chat/booking, potential slow response
if (websiteData.hasContactForm && !websiteData.hasChatWidget && !websiteData.hasBookingTool) {
  signals.push({
    id: 'inboundResponseRisk',
    label: 'Inbound Response Risk',
    detected: true,
    value: 'Form-only contact - no live chat or booking',
    confidence: 70
  });
}

// Calculate score
const scoreWeights = {
  googlePaidTraffic: 40,
  afterHoursCoverage: 30,
  inboundResponseRisk: 25
};

let score = 0;
for (const signal of signals) {
  const weight = scoreWeights[signal.id] || 0;
  score += Math.round(weight * (signal.confidence / 100));
}

// Generate "Why Now" message
let whyNow = 'Signal activity detected';
const signalIds = signals.map(s => s.id);

if (signalIds.includes('googlePaidTraffic') && signalIds.includes('afterHoursCoverage')) {
  whyNow = 'Paying for ads but potential after-hours coverage gap';
} else if (signalIds.includes('googlePaidTraffic') && signalIds.includes('inboundResponseRisk')) {
  whyNow = 'Ad spend at risk due to slow response setup';
} else if (signalIds.includes('googlePaidTraffic')) {
  whyNow = 'Active Google Ads spend detected';
} else if (signalIds.includes('afterHoursCoverage')) {
  whyNow = 'After-hours coverage gap identified';
} else if (signalIds.includes('inboundResponseRisk')) {
  whyNow = 'Inbound response at risk';
}

return [{
  json: {
    domain,
    originalDomain: adsData.originalDomain,
    score,
    signals,
    signalCount: signals.length,
    whyNow,
    websiteAnalysis: websiteData,
    checkedAt: new Date().toISOString()
  }
}];
